import { useEffect, useState } from "react";
import Banner from "./components/Banner/Banner";
import DataGrid from "./components/DataGrid/DataGrid";
import DetailInformation from "./components/DetailInformation/DetailInformation";
import SearchForm from "./components/Search/SearchForm";
import { BrowserRouter, Route, Routes } from "react-router-dom";

function App() {
  const [data, setData] = useState("");
  const [filterData, setFilterData] = useState([]);
  useEffect(() => {
    fetchData();
  }, []);
  const fetchData = async () => {
    try {
      const response = await fetch(`https://api.spacexdata.com/v3/capsules`);
      console.log(response);
      const dataResponse = await response.json();
      console.log(dataResponse);
      setData(dataResponse);
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };
  const filteredData = (searchParams) => {
    console.log(searchParams);
    const { type, serial, status, landings } = searchParams;
    console.log(landings);
    if (type && serial && status && landings) {
      const filterdata = data.filter(
        (item) =>
          item.capsule_serial === serial &&
          item.status === status &&
          item.landings == landings &&
          item.type === type
      );
      setFilterData(filterdata);
    } else if (serial && status && landings) {
      const filterdata = data.filter(
        (item) =>
          item.capsule_serial === serial &&
          item.status === status &&
          item.landings == landings
      );
      setFilterData(filterdata);
    } else if (type && serial && status) {
      const filterdata = data.filter(
        (item) =>
          item.capsule_serial === serial &&
          item.status === status &&
          item.type === type
      );
      setFilterData(filterdata);
    } else if (type && serial && landings) {
      const filterdata = data.filter(
        (item) =>
          item.capsule_serial === serial &&
          item.landings == landings &&
          item.type === type
      );
      setFilterData(filterdata);
    } else if (type && status && landings) {
      const filterdata = data.filter(
        (item) =>
          item.status === status &&
          item.landings == landings &&
          item.type === type
      );
      setFilterData(filterdata);
    } else if (serial && status && landings) {
      const filterdata = data.filter(
        (item) =>
          item.capsule_serial === serial &&
          item.landings == landings &&
          item.status === status
      );
      setFilterData(filterdata);
    } else if (serial && status) {
      const filterdata = data.filter(
        (item) => item.status === status && item.capsule_serial === serial
      );
      setFilterData(filterdata);
    } else if (serial && landings) {
      const filterdata = data.filter(
        (item) => item.landings == landings && item.capsule_serial === serial
      );

      setFilterData(filterdata);
    } else if (serial && type) {
      const filterdata = data.filter((item) => {
        return item.type === type && item.capsule_serial === serial;
      });
      setFilterData(filterdata);
    } else if (landings && status) {
      const filterdata = data.filter(
        (item) => item.status === status && item.landings == landings
      );
      setFilterData(filterdata);
    } else if (landings && type) {
      const filterdata = data.filter(
        (item) => item.type === type && item.landings == landings
      );
      setFilterData(filterdata);
    } else if (status && type) {
      const filterdata = data.filter(
        (item) => item.status === status && item.type === type
      );
      setFilterData(filterdata);
    } else if (serial) {
      const filterdata = data.filter((item) => item.capsule_serial === serial);
      setFilterData(filterdata);
    } else if (type) {
      const filterdata = data.filter((item) => item.type === type);
      setFilterData(filterdata);
    } else if (status) {
      const filterdata = data.filter((item) => item.status === status);
      setFilterData(filterdata);
    } else if (landings) {
      const filterdata = data.filter((item) => item.landings == landings);
      console.log(landings);
      setFilterData(filterdata);
    } else {
      console.log(filterData);
      setFilterData(data);
    }
  };

  const handleSearch = (searchParams) => {
    filteredData(searchParams);
  };
  console.log(filterData);

  return (
    <Router>
      <div className="App">
        <Banner />
        <SearchForm onSearch={handleSearch} />

        <Routes>
          {/* Define your routes here */}
          <Route
            path="/"
            element={
              <DataGrid
                data={filterData.length > 0 ? filterData : data}
                itemsPerPage={10}
              />
            }
          />
          <Route path="/info" element={<DetailInformation />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
