import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./DataGrid.css";
function DataGrid({ data, itemsPerPage }) {
  // const navigate = useNavigate();
  const [currentPage, setCurrentPage] = useState(1);

  if (!data || data.length === 0) {
    return <p>No data available.</p>;
  }
  const totalPages = Math.ceil(data.length / itemsPerPage);
  console.log(totalPages);
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = data.slice(indexOfFirstItem, indexOfLastItem);
  console.log(currentItems);
  const handlePageChange = (page) => {
    setCurrentPage(page);
  };
  return (
    <div className="datagrid-container">
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Original_Launch</th>
            <th>Details</th>
            <th>Landings</th>
            <th>Type</th>
            <th>Serial</th>
            <th>Status</th>
            <th>Count</th>
          </tr>
        </thead>
        <tbody>
          {currentItems?.map((item, id) => (
            <tr key={id}>
              <td>{item.capsule_id}</td>
              <td>{new Date(item.original_launch).toLocaleDateString()}</td>
              <td>{item.details}</td>
              <td>{item.landings}</td>
              <td>{item.type}</td>
              <td>{item.capsule_serial}</td>
              <td>{item.status}</td>
              <td>{item.reuse_count}</td>
              <td>
                <Link to="/info/item.capsule_id">Details</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="pagination">
        {Array.from({ length: totalPages }, (_, index) => (
          <button
            key={index + 1}
            onClick={() => handlePageChange(index + 1)}
            className={currentPage === index + 1 ? "active" : ""}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
}

export default DataGrid;
