import { useEffect, useState } from "react";
import Banner from "./components/Banner/Banner";
import DataGrid from "./components/DataGrid/DataGrid";
import SearchForm from "./components/Search/SearchForm";

function App() {
  const [data, setData] = useState("");
  const [filterData, setFilterData] = useState([]);
  useEffect(() => {
    fetchData();
  }, []);
  const fetchData = async (searchParams) => {
    try {
      const response = await fetch(`https://api.spacexdata.com/v3/capsules`, {
        params: searchParams,
      });
      const dataResponse = await response.json();
      setData(dataResponse);
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };
  const filteredData = (searchParams) => {
    console.log(searchParams);
    const { type, original_launch, status } = searchParams;
    if (type && original_launch && status) {
      const filterdata = data.filter((item) => {
        return (
          item.status === status &&
          item.original_launch === original_launch &&
          item.type === type
        );
      });
      setFilterData(filterdata);
    }
    if (original_launch && status) {
      const filterdata = data.filter((item) => {
        return (
          item.status === status && item.original_launch === original_launch
        );
      });
      setFilterData(filterdata);
    }
    if (status && type) {
      const filterdata = data.filter((item) => {
        return item.status === status && item.type === type;
      });
      setFilterData(filterdata);
    }
    if (original_launch && type) {
      const filterdata = data.filter((item) => {
        return item.type === type && item.original_launch === original_launch;
      });
      setFilterData(filterdata);
    }
    if (original_launch) {
      const filterdata = data.filter((item) => {
        return item.original_launch === original_launch;
      });
      setFilterData(filterdata);
    }
    if (type) {
      const filterdata = data.filter((item) => {
        return item.type === type;
      });
      setFilterData(filterdata);
    }
    if (status) {
      const filterdata = data.filter((item) => {
        return item.status === status;
      });
      setFilterData(filterdata);
    }
  };

  const handleSearch = (searchParams) => {
    filteredData(searchParams);
  };

  return (
    <div className="App">
      <Banner />
      <SearchForm onSearch={handleSearch} />
      <DataGrid data={filterData} itemsPerPage={10} />
    </div>
  );
}

export default App;
