import { useEffect, useState } from "react";
import Banner from "./components/Banner/Banner";
import DataGrid from "./components/DataGrid/DataGrid";
import SearchForm from "./components/Search/SearchForm";

function App() {
  const [data, setData] = useState("");
  const [filterData, setFilterData] = useState([]);
  useEffect(() => {
    fetchData();
  }, []);
  const fetchData = async () => {
    try {
      const response = await fetch(`https://api.spacexdata.com/v3/capsules`);
      console.log(response);
      const dataResponse = await response.json();
      console.log(dataResponse);
      setData(dataResponse);
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };
  const filteredData = (searchParams) => {
    console.log(searchParams);
    const { type, serial, status } = searchParams;
    if (type && serial && status) {
      const filterdata = data.filter(
        (item) =>
          item.status === status &&
          item.capsule_serial === serial &&
          item.type === type
      );
      setFilterData(filterdata);
    } else if (serial && status) {
      const filterdata = data.filter(
        (item) => item.status === status && item.capsule_serial === serial
      );
      setFilterData(filterdata);
    } else if (status && type) {
      const filterdata = data.filter(
        (item) => item.status === status && item.type === type
      );
      setFilterData(filterdata);
    } else if (serial && type) {
      const filterdata = data.filter((item) => {
        return item.type === type && item.capsule_serial === serial;
      });
      setFilterData(filterdata);
    } else if (serial) {
      const filterdata = data.filter((item) => item.capsule_serial === serial);
      setFilterData(filterdata);
    } else if (type) {
      const filterdata = data.filter((item) => item.type === type);
      setFilterData(filterdata);
    } else if (status) {
      const filterdata = data.filter((item) => item.status === status);
      setFilterData(filterdata);
    } else {
      console.log(filterData);
      setFilterData(data);
    }
  };

  const handleSearch = (searchParams) => {
    filteredData(searchParams);
  };
  console.log(filterData);

  return (
    <div className="App">
      <Banner />
      <SearchForm onSearch={handleSearch} />

      <DataGrid
        data={filterData.length === 0 ? filterData : data}
        itemsPerPage={10}
      />
    </div>
  );
}

export default App;
